cgfp.program:


      float4 main (in float2 TexCoord : TEXCOORD0,
		uniform sampler2D Texture1,
		uniform float threshold,
		uniform float2 hdrScale) : COLOR
      {
	      float4 src = tex2D (Texture1, TexCoord);
	      float3 thresholded = max ((src.rgb * hdrScale.x - float3 (threshold))/(1-threshold), 0);
	      return float4 (thresholded* hdrScale.y, src.a);
      }
