cgfp.program:


      float4 main (in float2 TexCoord : TEXCOORD0,
		uniform sampler2D Texture1) : COLOR
      {
	      float threshold = 0.8; // FIXME: un-hardcode
	      float4 src = tex2D (Texture1, TexCoord);
	      float3 thresholded = max ((src.rgb - float3 (threshold))/(1-threshold), 0);
	      return float4 (thresholded, src.a);
      }
