cgfp.program:




uniform sampler2D g_TexY;
uniform sampler2D g_TexABC;

float2 g_Resolution;
float2 g_InvResolution;


float4 main(float2 texUV: TEXCOORD0) : COLOR
{
	float3 abc1 = tex2D (g_TexABC, float2(texUV.x, 0.25)).xyz;
	float3 abc2 = tex2D (g_TexABC, float2(texUV.x, 0.75)).xyz;

	float3 y1 = tex2D (g_TexY, float2(texUV.x, 0.25)).xyz;
	float3 y2 = tex2D (g_TexY, float2(texUV.x, 0.75)).xyz;

	float inv_det = 1.0 / (abc1.y*abc2.y - abc1.z*abc2.x);
	float3 color;

	if ((texUV.y - g_InvResolution.y) < 0.0 )
		color = inv_det*(abc2.y*y1 - abc1.z*y2);
	else
		color = inv_det*(abc1.y*y2 - abc2.x*y1);

	return float4(color, 1.0);
}
