cgfp.program:


    float4 main (in float2 TexCoord : TEXCOORD0,
	      uniform sampler2D Texture1, uniform sampler2D Texture2,
	      uniform float2 hdrScale) : COLOR
    {
	    float4 a = tex2D (Texture1, TexCoord);
	    float4 b = tex2D (Texture2, TexCoord)*hdrScale.x;
	    return a+b*b*hdrScale.y;
    }
